const VenuePage = () => {
    const { id } = useParams();
    const [venue, setVenue] = useState(null);
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [bookedDates, setBookedDates] = useState([]);
    const [guests, setGuests] = useState(1);
    const [bookingError, setBookingError] = useState(null);
    const [isBookingLoading, setIsBookingLoading] = useState(false);

    useEffect(() => {
        const getVenue = async (id) => {
            const response = await fetch(
                `https://api.noroff.dev/api/v1/holidaze/venues/${id}`
            );
            const data = await response.json();

            if (data && data.bookings) {
                const bookedDateRanges = data.bookings.map((booking) => ({
                    startDate: new Date(booking.dateFrom),
                    endDate: new Date(booking.dateTo),
                }));
                setBookedDates(bookedDateRanges);
            }

            return data;
        };

        getVenue(id).then((data) => setVenue(data));
    }, [id]);

    const handleSelect = (ranges) => {
        setStartDate(ranges.selection.startDate);
        setEndDate(ranges.selection.endDate);
    };

    const handleGuestsChange = (e) => {
        setGuests(parseInt(e.target.value));
    };

    const handleBooking = async () => {
        setIsBookingLoading(true);
        setBookingError(null);

        try {
            const bookingData = {
                dateFrom: startDate.toISOString(),
                dateTo: endDate.toISOString(),
                guests: guests,
                venueId: id,
            };

            const response = await fetch(
                "https://api.noroff.dev/api/v1/holidaze/bookings",
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify(bookingData),
                }
            );

            if (!response.ok) {
                throw new Error("Booking failed. Please try again.");
            }

            // Reset form values
            setStartDate(new Date());
            setEndDate(new Date());
            setGuests(1);
        } catch (error) {
            setBookingError(error.message);
        } finally {
            setIsBookingLoading(false);
        }
    };

    if (!venue) return <div>Loading...</div>;

    return (
        <VenueContainer>
            <Carousel>
                {venue.media.map((img, index) => (
                    <div key={index}>
                        <img src={img} alt={`Image ${index}`} />
                    </div>
                ))}
            </Carousel>
            <VenueInfo>
                <h2>{venue.name}</h2>
                <p>{venue.description}</p>
                <p>Price: {venue.price}</p>
                <p>Max guests: {venue.maxGuests}</p>
                <p>Rating: {venue.rating}</p>
                <p>Address: {venue.location.address}</p>
                <p>City: {venue.location.city}</p>
            </VenueInfo>
            <DateRangePickerContainer>
                <DateRangePicker
                    ranges={[
                        {
                            startDate: startDate,
                            endDate: endDate,
                            key: "selection",
                        },
                    ]}
                    onChange={handleSelect}
                    minDate={new Date()}
                    disabledDates={bookedDates}
                    showSelectionPreview={true}
                    moveRangeOnFirstSelection={false}
                />
            </DateRangePickerContainer>
            <GuestsInputContainer>
                <label htmlFor="guests">Guests:</label>
                <select
                    id="guests"
                    value={guests}
                    onChange={handleGuestsChange}
                >
                    <option value={1}>1 Guest</option>
                    <option value={2}>2 Guests</option>
                    <option value={3}>3 Guests</option>
                    <option value={4}>4 Guests</option>
                </select>
            </GuestsInputContainer>
            <BookingButton onClick={handleBooking} disabled={isBookingLoading}>
                {isBookingLoading ? "Booking..." : "Book Now"}
            </BookingButton>
            {bookingError && <ErrorMessage>{bookingError}</ErrorMessage>}
        </VenueContainer>
    );
};

export default VenuePage;

const VenueContainer = styled.div`
    /* Styles for venue container */
`;

const VenueInfo = styled.div`
    /* Styles for venue information */
`;

const DateRangePickerContainer = styled.div`
    /* Styles for date range picker container */
`;

const GuestsInputContainer = styled.div`
    /* Styles for guests input container */
    display: flex;
    align-items: center;
    gap: 1rem;

    label {
        font-weight: bold;
    }

    select {
        padding: 0.5rem;
    }
`;

const BookButton = styled.button`
    padding: 1rem 2rem;
    background-color: var(--primary);
    color: white;
    font-weight: bold;
    border-radius: 10px;
    cursor: pointer;
    border: none;
    transition: background-color 0.3s ease;

    &:hover {
        background-color: var(--secondary);
        color: black;
    }

    &:disabled {
        background-color: #ccc;
        cursor: not-allowed;
    }
`;
