import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import DataSetup from "../../hooks/dataSetup";
import { DateRangePicker } from "react-date-range";
import "react-date-range/dist/styles.css"; // main style file
import "react-date-range/dist/theme/default.css"; // theme css file

const VenuePage = () => {
    const { id } = useParams(); // Henter id fra URL
    const [venue, setVenue] = useState(null);
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());

    useEffect(() => {
        const getVenue = async (id) => {
            const response = await fetch(
                `https://api.noroff.dev/api/v1/holidaze/venues/${id}`
            );
            const data = await response.json();
            return data;
        };
        getVenue(id).then((data) => setVenue(data));
    }, [id]);

    if (!venue) return <div>Loading...</div>;

    const handleSelect = (ranges) => {
        setStartDate(ranges.selection.startDate);
        setEndDate(ranges.selection.endDate);
    };

    const selectionRange = {
        startDate: startDate,
        endDate: endDate,
        key: "selection",
    };

    return (
        <VenueContainer>
            <img src={venue.media} alt={venue.name} />
            <h2>{venue.name}</h2>
            <p>{venue.description}</p>
            <p>Price: {venue.price}</p>
            <p>Max guests: {venue.maxGuests}</p>
            <p>Rating: {venue.rating}</p>
            <p>Address: {venue.location.address}</p>
            <p>City: {venue.location.city}</p>
            <DateRangePicker
                ranges={[selectionRange]}
                onChange={handleSelect}
            />
        </VenueContainer>
    );
};

export default VenuePage;

const VenueContainer = styled.div`
    /* Legg til dine styles her */
`;
