import React, { useContext } from "react";
import { AuthContext } from "../../hooks/context";
import styled from "styled-components";
import UserBookings from "../../components/userBookings";
import VenueManagerControls from "../../components/venueManger";

const ProfilePage = () => {
    const { user, accessToken } = useContext(AuthContext);

    return (
        <ProfileContainer>
            <AvatarSection>
                <Avatar src={user.avatar} alt={`${user.name}'s avatar`} />
                <UploadAvatarButton>Upload new avatar</UploadAvatarButton>
            </AvatarSection>
            <h1>{user.name}'s Profile</h1>
            {user.role === "venue-manager" ? (
                <VenueManagerControls user={user} token={accessToken} />
            ) : (
                <UserBookings user={user} token={accessToken} />
            )}
        </ProfileContainer>
    );
};

export default ProfilePage;

const ProfileContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 2rem;
`;

const AvatarSection = styled.section`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1rem;
`;

const Avatar = styled.img`
    border-radius: 50%;
    width: 150px;
    height: 150px;
`;

const UploadAvatarButton = styled.button`
    padding: 0.5rem 1rem;
    border: none;
    border-radius: 5px;
    background-color: #4caf50;
    color: white;
    cursor: pointer;
    transition: 0.3s;

    &:hover {
        background-color: #45a049;
    }
`;
