import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import { DateRangePicker } from "react-date-range";
import { Carousel } from "react-responsive-carousel";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { useContext } from "react";
import { AuthContext } from "../..context/";

const VenuePage = () => {
    const { id } = useParams(); // Henter id fra URL
    const [venue, setVenue] = useState(null);
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [disabledDates, setDisabledDates] = useState([]);

    const { isLoggedIn } = useContext(AuthContext);

    useEffect(() => {
        const getVenue = async (id) => {
            const response = await fetch(
                `https://api.noroff.dev/api/v1/holidaze/venues/${id}`
            );
            const data = await response.json();
            setDisabledDates(
                data.bookings.map(
                    (booking) => new Date(booking.dateFrom),
                    new Date(booking.dateTo)
                )
            );
            return data;
        };
        getVenue(id).then((data) => setVenue(data));
    }, [id]);

    if (!venue) return <div>Loading...</div>;

    const handleSelect = (ranges) => {
        setStartDate(ranges.selection.startDate);
        setEndDate(ranges.selection.endDate);
    };

    const selectionRange = {
        startDate: startDate,
        endDate: endDate,
        key: "selection",
    };

    return (
        <VenueContainer>
            <Carousel>
                {venue.media.map((img, index) => (
                    <div key={index}>
                        <img src={img} />
                    </div>
                ))}
            </Carousel>
            <h2>{venue.name}</h2>
            <p>{venue.description}</p>
            <p>Price: {venue.price}</p>
            <p>Max guests: {venue.maxGuests}</p>
            <p>Rating: {venue.rating}</p>
            <p>Address: {venue.location.address}</p>
            <p>City: {venue.location.city}</p>
            <DateRangePicker
                ranges={[selectionRange]}
                onChange={handleSelect}
                disabledDates={disabledDates}
            />
            <button disabled={!isLoggedIn}>Book Now</button>
        </VenueContainer>
    );
};

export default VenuePage;

const VenueContainer = styled.div`
    /* Legg til dine styles her */
`;
